new Vue({el:"#app",data:{quizList:[],question:"",choices:[],collectIdx:0,commentary:"",contentLoaded:!1},methods:{answer:function(t){parseInt(t.target.dataset.idx)===this.collectIdx?(alert("正解!!"),alert(this.commentary),this.nextQuestion()):(alert("不正解!!"),t.target.style.display="none")},nextQuestion:function(){0===this.quizList.length&&(alert("おしまい!! お疲れ様でした!!"),window.location.reload());const t=this.quizList.shift();this.question=t.question,this.choices=t.choices,this.collectIdx=t.collectIdx,this.commentary=t.commentary},loadQuize:function(){return new Promise(((t,e)=>{axios.get("./data.json").then((e=>{this.quizList=e.data,t()})).catch((t=>{e(t)}))}))}},mounted:function(){this.loadQuize().then((()=>{this.nextQuestion()})).catch((t=>{console.log(t),this.question="クイズの取得に失敗しました。"})),this.contentLoaded=!0}});